import db from '../../repository';

const ref = db().collection('quiz');

const getQuiz = async (id) => {
  const quiz = await ref.doc(id).get();
  if (quiz.exists) {
    console.log('EXISSTS!!!');
    return quiz.data();
  }
  console.log('DOESNT EXISTS');
  return {};
};

export default { getQuiz };
