import React, { useState } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';
import Bootstrap from '../../components/bootstrap';
import Head from 'next/head';
// import { Container } from 'react-bootstrap';
import { Background, MyContainer } from './styles';

const Quiz = () => {
  const [counter, setCounter] = useState(0);

  return (
    <>
      <Head>
        <title>Quiz</title>
        {/* <link href="/public/quiz/styles.css" rel="stylesheet" /> */}
        {Bootstrap}
      </Head>
      <div className="row background">
        <div className="container">
          <div className="card darken-1">
            <div className="card-content">
              <span id="title" className="card-title">
                Que tipo de empreendedor é você durante a crise?
              </span>
            </div>
            <div className="card-action">
              <p id="question"></p>
              <form id="answers" action="#"></form>
            </div>
            <div className="card-footer">
              <hr />
              <a className="waves-effect waves-light btn" id="btnNextQuestion">
                Próxima Pergunta
              </a>
              <ul className="pagination">
                <li className="disabled waves-effect">
                  <a href="#!" id="btnPrev">
                    <i className="material-icons">chevron_left</i>
                  </a>
                </li>
                <li className="waves-effect">
                  <a href="#!" id="btnNext">
                    <i className="material-icons">chevron_right</i>
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>

      <style jsx>
        {`
          * {
            margin: 0;
            padding: 0;
          }

          .row {
            margin: 0;
          }

          .background {
            width: 100vw;
            min-height: 100vh;
            background-color: #3498db;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          @media screen and (min-width: 800px) {
            .container {
              width: 60vw;
              border-radius: 20px;
            }

            .card {
              padding: 60px;
            }
          }

          @media screen and (max-width: 800px) {
            .container {
              width: 95vw;
            }

            .card {
              padding: 10px;
            }
          }

          .rdText {
            color: #333;
          }

          .correct {
            color: #27ae60;
          }

          .almost {
            color: #f1c40f;
          }

          .wrong {
            color: #e74c3c;
          }

          .rdContainer {
            margin: 10px 0;
          }

          hr {
            border-top: 1px solid rgba(160, 160, 160, 0.2);
          }

          .pagination {
            padding: 20px 0;
            display: flex;
            align-items: center;
            justify-content: center;
          }

          .pagination li.active {
            background-color: #3498db;
          }

          #btnNextQuestion {
            overflow-wrap: normal;
          }

          .card-footer {
            display: flex;
            justify-content: center;
            align-items: center;
            flex-direction: column;
          }

          .btn {
            margin: 30px 0;
            background-color: #34495e;
          }

          input[type='radio'].with-gap:checked + span:before,
          [type='radio'].with-gap:checked + span:after {
            border: 2px solid #3498db !important;
          }

          [type='radio']:checked + span:after,
          [type='radio'].with-gap:checked + span:after {
            background-color: #3498db !important;
          }

          #question {
            text-align: justify;
          }

          .btn:hover {
            background-color: #3498db;
          }

          .link {
            margin-right: 0 !important;
            text-transform: none !important;
          }

          img {
            padding: 20px 0;
          }
        `}
      </style>
    </>
  );
};

export default Quiz;
