import styled from 'styled-components';
import { Container } from 'react-bootstrap';

export const Background = styled.div`
  width: 100vw;
  min-height: 100vh;
  background-color: #3498db;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0;
`;

export const MyContainer = styled(Container)`
  width: 60vw;
  border-radius: 20px;
`;

