import React, {useState} from 'react'

const Quiz = () => {
  const [counter, setCounter] = useState(0);

  return ( 
    <div>
      Counter: {counter}<br />
      <button onClick={() => setCounter(counter+1)}>Aumentar</button>
  </div>
 );
}


export default Quiz;
